mapper.select:
select C_NO,  A_ID, C_TITLE, C_CONTENT, C_DETAIL_TITLE, C_DETAIL_CONTENT, C_DETAIL_VIDEOID, CAT_TITLE, C_FILE, C_THUMB,c_views
from CAMPAIGN_BOARD
WHERE c_no = #{c_no}

mapper.update:
update CAMPAIGN_BOARD
SET C_TITLE=#{c_title}, C_CONTENT=#{c_content}, C_DETAIL_TITLE=#{c_detail_title}, C_DETAIL_CONTENT=#{c_detail_content}, 
C_DETAIL_VIDEOID=#{c_detail_videoid}, CAT_TITLE=#{cat_title} 

	,C_THUMB=#{c_thumb}


	,C_FILE = #{c_file}

where c_no = #{c_no}

update CAMPAIGN_BOARD
set c_views = c_views + 1
where c_no = #{c_no}
