mapper.select:
select m_no, m_c_name, m_title, id, m_views, to_char(m_date,'YYYY-MM-DD') as m_date, m_content, m_thumb, m_file
from mission
where m_no = #{m_no}

mapper.update:
update mission
set m_c_name=#{m_c_name}, m_title =#{m_title}, m_content =#{m_content}, m_date = sysdate

,m_thumb =#{m_thumb}


,m_file =#{m_file}

where m_no =#{m_no}

update mission
set m_views = m_views + 1
where m_no = #{m_no}
